mapper.update:
update mover_order_dispatch
set dispatch_status = #{dispatchStatus},
    dispatch_time = #{dispatchTime},
    response_time = #{responseTime},
    update_time = #{updateTime}
where order_id = #{orderId}

mapper.select:
select count(*) from mover_order_dispatch
where order_id = #{orderId}
and dispatch_status = 0

select count(*) from mover_order_dispatch
where mover_id = #{moverId}
and dispatch_status = 0
